package com.example.noticiero.Data

import android.annotation.SuppressLint
import android.app.Application
import android.util.Log.e
import androidx.room.Room
import com.example.noticiero.Entities.Noticia
import kotlinx.coroutines.CoroutineScope
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.launch

class Aplication : Application() {
    companion object {
        lateinit var baseDeDatos: NoticiaDatabase
    }

    override fun onCreate() {
        super.onCreate()

        baseDeDatos = Room.databaseBuilder(
            applicationContext,
            NoticiaDatabase::class.java,
            "noticias_database"
        )
            .fallbackToDestructiveMigration()
            .build()

        agregarLibros()
    }

    @SuppressLint("SuspiciousIndentation")
    private fun agregarLibros() {
        CoroutineScope(Dispatchers.IO).launch {
            try {
                val noticiasExistentes = baseDeDatos.bookDao().getAllBooks()

                if (noticiasExistentes.isEmpty()) {

                    val libros = listOf(
                        Noticia(
                            title = "Luka Modrić",
                            author = "09/09/1985",
                            description = "Es un futbolista croata que desde 2012 juega como centrocampista en el Real Madrid C. F. de la Primera División de España. Fue ganador del Balón de Oro y obtuvo el Premio The Best (al mejor) jugador del mundo según la FIFA en 2018. Con 27 títulos es el jugador con más títulos logrados a lo largo de la historia en el Real Madrid C. F.",
                            imageUrl = "data:image/jpeg;base64,",
                            esFavorita = false
                        ),
                        Noticia(
                            title = "Cristiano Ronaldo",
                            author = "05/02/1985",
                            description = "Es un futbolista portugués. Juega de delantero y su equipo actual es el Al-Nassr F. C. de la Liga Profesional Saudí. Es internacional absoluto con la selección de Portugal, de la cual es capitán y máximo goleador histórico.",
                            imageUrl = "data:image/jpeg;base64,",
                            esFavorita = false
                        ),
                        Noticia(
                            title = "Lionel Messi",
                            author = "24/06/1987",
                            description = "Es un futbolista argentino que juega como delantero o centrocampista. Desde 2023, integra el plantel del Inter Miami de la MLS canadoestadounidense. Es también internacional con la selección de Argentina, de la que es capitán.",
                            imageUrl = "data:image/jpeg;base64,",
                            esFavorita = false
                        ),
                        Noticia(
                            title = "Andrés Iniesta",
                            author = "11/05/1984",
                            description = "Es un exfutbolista español. Jugó en la posición de centrocampista, desarrollando la mayor parte de su carrera en el F. C. Barcelona y en la selección española, siendo el futbolista más laureado en la historia de su país, con un total de 40 títulos oficiales. Es ampliamente considerado como uno de los mejores futbolistas de su generación y uno de los mejores centrocampistas de la historia.",
                            imageUrl = "data:image/jpeg;base64,",
                            esFavorita = false
                        )
                    )

                    libros.forEach { libro ->
                        baseDeDatos.bookDao().addBook(libro)
                    }

                }


            } catch (e: Exception) {
                e.printStackTrace()
            }
        }
    }

}
